var validator = function(fieldToValidate){
		var res = false;
		var content = fieldToValidate.content;
		var criteria = fieldToValidate.criteria;
		var type = fieldToValidate.type;
		if(!isNull(content)){
			if(!isNull(criteria)){
				res = criteria(content); 
			}else if(!isNull(type)){
				res = executeDefaultValidation(content,type);
			}else{
				console.error('Type or criteria not given');
			}
		}else console.error('Error, no content given');
		return res;
	}

var executeDefaultValidation = function(content,type){
	var res = false;
	switch(type){
		case 'NAME':
			res = /^[^0-9 @#?|!]+$/.test(content);
		break;
		case 'SURNAME' :
			res = /^[^0-9@#?|!]+$/.test(content);
		break;
		case 'BIRTH_DATE' :
			res = /^\d{2}\/\d{2}\/\d{4}$/.test(content);
		break;
		case 'EMAIL' :
			res = /^[A-Za-z0-9_\-\.]+@[A-Za-z0-9]+\.[a-z]+$/.test(content);
		break;
		default :
			console.errro('No specific type found')
	}
	return res;
}

var isNull = function(p){
	return p===null || p===undefined || p==='' &&  0 === p.length;
}
	
module.exports.validator = validator;
