var validatorModule = require('newsl-validator');

nameField = {
	content : 'Alex',
	type : 'NAME'
}

surnameField = {
	content : 'O\'Red',
	type : 'SURNAME',
	criteria : function(content){
		return /^[^0-9@#?|!]+$/.test(content) && content.length < 2;
	}
}

birthDateField = {
	content : '08/02/1980',
	type : 'BIRTH_DATE'
}

emailField ={
	content : 'red.b@gmail.com',
	type : 'EMAIL'
}

var res = validatorModule.validator(nameField);
console.log('Result : ',res);

var res = validatorModule.validator(surnameField);
console.log('Result : ',res);

var res = validatorModule.validator(birthDateField);
console.log('Result : ',res);

var res = validatorModule.validator(emailField);
console.log('Result : ',res);
